from aiogram import types
from dispatcher import dp, bot
from random import randint
import asyncio
import sqlite3

@dp.message_handler(text=['Бот', 'бот', 'Кокос', 'кокос'])
async def process_giveeclairs_command(message: types.Message):
    ra = randint(1,7)
    if ra == 1:
        await message.answer("✅ тут!")
    elif ra == 2:
        await message.answer("✅ гав!")
    elif ra == 3:
        await message.answer("✅ здесь!")
    elif ra == 4:
        await message.answer("✅ гав гав гав!")
    elif ra == 5:
        await message.answer("✅ рядом!")
    elif ra == 6:
        await message.answer("✅ гаааааааааааааааааааав!")
    else:
        if message.text == "бот" or message.text == "Бот":
            await message.answer("✅ привет!")
        else:
            await message.answer("✅ чо?")


