@dp.message_handler(commands=['msg'])
async def chapcha_expression(message: types.Message):
    if True:
        try:
            comm = message.text[5:]
            ui = message.from_user.id
            gi = message.text.split(' ')[1]
            msg = comm[len(gi)+1:]
            try:
                await bot.send_message(gi, f"<b>нновое сообщение от <a href='tg://user?id={ui}'>{message.from_user.first_name}</a></b> (<code>{ui}</code>)\n{msg}\n\n<code>/msg {ui}</code> Текст - ответить", parse_mode="HTML")
                await message.answer("сообщение отправлено!")
            except:
                await message.answer("пользователь не найден или не пользуется ботом. Сообщение не отправлено")
        except:
            await message.answer("/msg ID Текст")

@dp.message_handler(commands=['say'])
async def chapcha_expression(message: types.Message):
    try:
        await message.delete()
        await message.answer(message.text[5:])
    except:
        pass